static sc_integer main_region_CERRADO_react(Porton* handle, const sc_integer transitioned_before)
{
	/* The reactions of state CERRADO. */
 			sc_integer transitioned_after = transitioned_before;
	if ((transitioned_after) < (1))
	{ 
		if (((handle->internal.siTECXOK_raised) == bool_true) && (((handle->internal.viTecla) == (PORTON_PORTONIFACE_TEC4)) == bool_true))
		{ 
			exseq_main_region_CERRADO(handle);
			porton_opLED(handle, PORTON_PORTONIFACE_LED3, PORTON_PORTONIFACE_LED_OFF);
			enseq_main_region_ABRIENDO_default(handle);
			react(handle, 0);
			transitioned_after = 1;
		} 
	} /* If no transition was taken then execute local reactions */
	if ((transitioned_after) == (transitioned_before))
	{ 
		transitioned_after = react(handle, transitioned_before);
	} return transitioned_after;
}

