static void porton_eventqueue_init(porton_eventqueue * eq, porton_event *buffer, sc_integer capacity)
{
	eq->events = buffer;
	eq->capacity = capacity;
	eq->push_index = 0;
	eq->pop_index = 0;
	eq->size = 0;
}

static sc_integer porton_eventqueue_size(porton_eventqueue * eq)
{
	return eq->size;
}

static porton_event porton_eventqueue_pop(porton_eventqueue * eq)
{
	porton_event event;
	if(porton_eventqueue_size(eq) <= 0) {
		porton_event_init(&event, Porton_invalid_event);
	}
	else {
		event = eq->events[eq->pop_index];
		
		if(eq->pop_index < eq->capacity - 1) {
			eq->pop_index++;
		} 
		else {
			eq->pop_index = 0;
		}
		eq->size--;
	}
	return event;
}
static sc_boolean porton_eventqueue_push(porton_eventqueue * eq, porton_event ev)
{
	if(porton_eventqueue_size(eq) == eq->capacity) {
		return bool_false;
	}
	else {
		eq->events[eq->push_index] = ev;
		
		if(eq->push_index < eq->capacity - 1) {
			eq->push_index++;
		}
		else {
			eq->push_index = 0;
		}
		eq->size++;
		
		return bool_true;
	}
}
static void porton_event_init(porton_event * ev, PortonEventID name)
{
	ev->name = name;
	ev->has_value = bool_false;
}

static void porton_add_event_to_queue(porton_eventqueue * eq, PortonEventID name)
{
	porton_event event;
	porton_event_init(&event, name);
	porton_eventqueue_push(eq, event);
}

static void porton_dispatch_event(Porton* handle, const porton_event * event) {
	switch(event->name) {
		case Porton_evTECXNoOprimido:
		{
			handle->iface.evTECXNoOprimido_raised = bool_true;
			break;
		}
		case Porton_evTECXOprimido:
		{
			handle->iface.evTECXOprimido_raised = bool_true;
			handle->iface.evTECXOprimido_value = event->value.Porton_evTECXOprimido_value;
			break;
		}
		case Porton_internal_siTECXOK:
		{
			handle->internal.siTECXOK_raised = bool_true;
			break;
		}
		case Porton_Porton_TECX_DEBOUNCE_time_event_0:
		{
			handle->timeEvents.porton_TECX_DEBOUNCE_tev0_raised = bool_true;
			break;
		}
		case Porton_Porton_main_region_ABIERTO_time_event_0:
		{
			handle->timeEvents.porton_main_region_ABIERTO_tev0_raised = bool_true;
			break;
		}
		case Porton_Porton_main_region_CERRANDO_time_event_0:
		{
			handle->timeEvents.porton_main_region_CERRANDO_tev0_raised = bool_true;
			break;
		}
		default:
			break;
	}
}

static porton_event porton_get_next_event(Porton* handle)
{
	porton_event next_event;
	porton_event_init(&next_event, Porton_invalid_event);
	if(porton_eventqueue_size(&(handle->internal_event_queue)) > 0) {
		next_event = porton_eventqueue_pop(&(handle->internal_event_queue));
	}
	else if(porton_eventqueue_size(&(handle->in_event_queue)) > 0) {
		next_event = porton_eventqueue_pop(&(handle->in_event_queue));
	}
	return next_event;
}

static void porton_dispatch_next_event(Porton* handle)
{
	porton_event nextEvent;
	nextEvent = porton_get_next_event(handle);
	porton_dispatch_event(handle, &nextEvent);
}

static PortonEventID porton_get_timed_event_name(Porton* handle, sc_eventid evid)
{
	if(evid == &handle->timeEvents.porton_TECX_DEBOUNCE_tev0_raised) {
		return Porton_Porton_TECX_DEBOUNCE_time_event_0;
	}
	if(evid == &handle->timeEvents.porton_main_region_ABIERTO_tev0_raised) {
		return Porton_Porton_main_region_ABIERTO_time_event_0;
	}
	if(evid == &handle->timeEvents.porton_main_region_CERRANDO_tev0_raised) {
		return Porton_Porton_main_region_CERRANDO_time_event_0;
	}
	return Porton_invalid_event;
}


static void porton_event_value_init(porton_event * ev, PortonEventID name, void * value)
{
	ev->name = name;
	ev->has_value = bool_true;
	
	switch(name)
	{
		case Porton_evTECXOprimido:
			ev->value.Porton_evTECXOprimido_value = *((sc_integer*)value);
			break;
		default:
			break;
	}
}

static void porton_add_value_event_to_queue(porton_eventqueue * eq, PortonEventID name, void * value)
{
	porton_event event;
	porton_event_value_init(&event, name, value);
	porton_eventqueue_push(eq, event);
}
