static sc_integer react(Ej5* handle, const sc_integer transitioned_before)
{
	/* State machine reactions. */
	SC_UNUSED(handle);
	return transitioned_before;
}

static sc_integer Porton_CERRADO_react(Ej5* handle, const sc_integer transitioned_before)
{
	/* The reactions of state CERRADO. */
 			sc_integer transitioned_after = transitioned_before;
	if ((transitioned_after) < (0))
	{ 
		if (handle->iface.evRemotoAbrir_raised == bool_true)
		{ 
			exseq_Porton_CERRADO(handle);
			ej5_add_event_to_queue(&(handle->internal_event_queue), Ej5_internal_siTitilar)
			;
			enseq_Porton_ABRIENDO_default(handle);
			transitioned_after = 0;
		} 
	} return transitioned_after;
}

