static void ej5_eventqueue_init(ej5_eventqueue * eq, ej5_event *buffer, sc_integer capacity)
{
	eq->events = buffer;
	eq->capacity = capacity;
	eq->push_index = 0;
	eq->pop_index = 0;
	eq->size = 0;
}

static sc_integer ej5_eventqueue_size(ej5_eventqueue * eq)
{
	return eq->size;
}

static ej5_event ej5_eventqueue_pop(ej5_eventqueue * eq)
{
	ej5_event event;
	if(ej5_eventqueue_size(eq) <= 0) {
		ej5_event_init(&event, Ej5_invalid_event);
	}
	else {
		event = eq->events[eq->pop_index];
		
		if(eq->pop_index < eq->capacity - 1) {
			eq->pop_index++;
		} 
		else {
			eq->pop_index = 0;
		}
		eq->size--;
	}
	return event;
}
static sc_boolean ej5_eventqueue_push(ej5_eventqueue * eq, ej5_event ev)
{
	if(ej5_eventqueue_size(eq) == eq->capacity) {
		return bool_false;
	}
	else {
		eq->events[eq->push_index] = ev;
		
		if(eq->push_index < eq->capacity - 1) {
			eq->push_index++;
		}
		else {
			eq->push_index = 0;
		}
		eq->size++;
		
		return bool_true;
	}
}
static void ej5_event_init(ej5_event * ev, Ej5EventID name)
{
	ev->name = name;
}

static void ej5_add_event_to_queue(ej5_eventqueue * eq, Ej5EventID name)
{
	ej5_event event;
	ej5_event_init(&event, name);
	ej5_eventqueue_push(eq, event);
}

static void ej5_dispatch_event(Ej5* handle, const ej5_event * event) {
	switch(event->name) {
		case Ej5_evRemotoAbrir:
		{
			handle->iface.evRemotoAbrir_raised = bool_true;
			break;
		}
		case Ej5_evRemotoCerrar:
		{
			handle->iface.evRemotoCerrar_raised = bool_true;
			break;
		}
		case Ej5_evEmergencia:
		{
			handle->iface.evEmergencia_raised = bool_true;
			break;
		}
		case Ej5_evFinCarrera:
		{
			handle->iface.evFinCarrera_raised = bool_true;
			break;
		}
		case Ej5_internal_siTitilar:
		{
			handle->internal.siTitilar_raised = bool_true;
			break;
		}
		case Ej5_internal_siNoTitilar:
		{
			handle->internal.siNoTitilar_raised = bool_true;
			break;
		}
		case Ej5_internal_siEmergencia:
		{
			handle->internal.siEmergencia_raised = bool_true;
			break;
		}
		case Ej5_ej5_Luz_Titilar_time_event_0:
		{
			handle->timeEvents.ej5_Luz_Titilar_tev0_raised = bool_true;
			break;
		}
		case Ej5_ej5_Luz_Emergencia_time_event_0:
		{
			handle->timeEvents.ej5_Luz_Emergencia_tev0_raised = bool_true;
			break;
		}
		default:
			break;
	}
}

static ej5_event ej5_get_next_event(Ej5* handle)
{
	ej5_event next_event;
	ej5_event_init(&next_event, Ej5_invalid_event);
	if(ej5_eventqueue_size(&(handle->internal_event_queue)) > 0) {
		next_event = ej5_eventqueue_pop(&(handle->internal_event_queue));
	}
	else if(ej5_eventqueue_size(&(handle->in_event_queue)) > 0) {
		next_event = ej5_eventqueue_pop(&(handle->in_event_queue));
	}
	return next_event;
}

static void ej5_dispatch_next_event(Ej5* handle)
{
	ej5_event nextEvent;
	nextEvent = ej5_get_next_event(handle);
	ej5_dispatch_event(handle, &nextEvent);
}

static Ej5EventID ej5_get_timed_event_name(Ej5* handle, sc_eventid evid)
{
	if(evid == &handle->timeEvents.ej5_Luz_Titilar_tev0_raised) {
		return Ej5_ej5_Luz_Titilar_time_event_0;
	}
	if(evid == &handle->timeEvents.ej5_Luz_Emergencia_tev0_raised) {
		return Ej5_ej5_Luz_Emergencia_time_event_0;
	}
	return Ej5_invalid_event;
}

