static sc_integer main_region_VALIDATION_ADD_DOWN_2_react(Escalera* handle, const sc_integer transitioned_before)
{
	/* The reactions of state VALIDATION_ADD_DOWN_2. */
 			sc_integer transitioned_after = transitioned_before;
	if ((transitioned_after) < (0))
	{ 
		if (handle->timeEvents.escalera_main_region_VALIDATION_ADD_DOWN_2_tev0_raised == bool_true)
		{ 
			exseq_main_region_VALIDATION_ADD_DOWN_2(handle);
			enseq_main_region_VALIDATION_DOWN_STOP_default(handle);
			react(handle, 0);
			transitioned_after = 0;
		}  else
		{
			if (handle->iface.evSenseAboveInner_raised == bool_true)
			{ 
				exseq_main_region_VALIDATION_ADD_DOWN_2(handle);
				handle->internal.viAmountPeople += 1;
				enseq_main_region_VALIDATION_DOWN_STOP_default(handle);
				react(handle, 0);
				transitioned_after = 0;
			} 
		}
	} /* If no transition was taken then execute local reactions */
	if ((transitioned_after) == (transitioned_before))
	{ 
		transitioned_after = react(handle, transitioned_before);
	} return transitioned_after;
}




static void escalera_eventqueue_init(escalera_eventqueue * eq, escalera_event *buffer, sc_integer capacity)
{
	eq->events = buffer;
	eq->capacity = capacity;
	eq->push_index = 0;
	eq->pop_index = 0;
	eq->size = 0;
}

static sc_integer escalera_eventqueue_size(escalera_eventqueue * eq)
{
	return eq->size;
}

static escalera_event escalera_eventqueue_pop(escalera_eventqueue * eq)
{
	escalera_event event;
	if(escalera_eventqueue_size(eq) <= 0) {
		escalera_event_init(&event, Escalera_invalid_event);
	}
	else {
		event = eq->events[eq->pop_index];
		
		if(eq->pop_index < eq->capacity - 1) {
			eq->pop_index++;
		} 
		else {
			eq->pop_index = 0;
		}
		eq->size--;
	}
	return event;
}
static sc_boolean escalera_eventqueue_push(escalera_eventqueue * eq, escalera_event ev)
{
	if(escalera_eventqueue_size(eq) == eq->capacity) {
		return bool_false;
	}
	else {
		eq->events[eq->push_index] = ev;
		
		if(eq->push_index < eq->capacity - 1) {
			eq->push_index++;
		}
		else {
			eq->push_index = 0;
		}
		eq->size++;
		
		return bool_true;
	}
}
static void escalera_event_init(escalera_event * ev, EscaleraEventID name)
{
	ev->name = name;
}

static void escalera_add_event_to_queue(escalera_eventqueue * eq, EscaleraEventID name)
{
	escalera_event event;
	escalera_event_init(&event, name);
	escalera_eventqueue_push(eq, event);
}

static void escalera_dispatch_event(Escalera* handle, const escalera_event * event) {
	switch(event->name) {
		case Escalera_evSenseAboveInner:
		{
			handle->iface.evSenseAboveInner_raised = bool_true;
			break;
		}
		case Escalera_evSenseAboveOuter:
		{
			handle->iface.evSenseAboveOuter_raised = bool_true;
			break;
		}
		case Escalera_evSenseBelowInner:
		{
			handle->iface.evSenseBelowInner_raised = bool_true;
			break;
		}
		case Escalera_evSenseBelowOuter:
		{
			handle->iface.evSenseBelowOuter_raised = bool_true;
			break;
		}
		case Escalera_escalera_main_region_VALIDATION_DOWN_STOP_time_event_0:
		{
			handle->timeEvents.escalera_main_region_VALIDATION_DOWN_STOP_tev0_raised = bool_true;
			break;
		}
		case Escalera_escalera_main_region_VALIDATION_UP_STOP_time_event_0:
		{
			handle->timeEvents.escalera_main_region_VALIDATION_UP_STOP_tev0_raised = bool_true;
			break;
		}
		case Escalera_escalera_main_region_VALIDATION_UP_START_time_event_0:
		{
			handle->timeEvents.escalera_main_region_VALIDATION_UP_START_tev0_raised = bool_true;
			break;
		}
		case Escalera_escalera_main_region_VALIDATION_DOWN_START_time_event_0:
		{
			handle->timeEvents.escalera_main_region_VALIDATION_DOWN_START_tev0_raised = bool_true;
			break;
		}
		case Escalera_escalera_main_region_VALIDATION_ADD_UP_1_time_event_0:
		{
			handle->timeEvents.escalera_main_region_VALIDATION_ADD_UP_1_tev0_raised = bool_true;
			break;
		}
		case Escalera_escalera_main_region_VALIDATION_ADD_UP_2_time_event_0:
		{
			handle->timeEvents.escalera_main_region_VALIDATION_ADD_UP_2_tev0_raised = bool_true;
			break;
		}
		case Escalera_escalera_main_region_VALIDATION_ADD_DOWN_1_time_event_0:
		{
			handle->timeEvents.escalera_main_region_VALIDATION_ADD_DOWN_1_tev0_raised = bool_true;
			break;
		}
		case Escalera_escalera_main_region_VALIDATION_ADD_DOWN_2_time_event_0:
		{
			handle->timeEvents.escalera_main_region_VALIDATION_ADD_DOWN_2_tev0_raised = bool_true;
			break;
		}
		default:
			break;
	}
}

static escalera_event escalera_get_next_event(Escalera* handle)
{
	escalera_event next_event;
	escalera_event_init(&next_event, Escalera_invalid_event);
	if(escalera_eventqueue_size(&(handle->in_event_queue)) > 0) {
		next_event = escalera_eventqueue_pop(&(handle->in_event_queue));
	}
	return next_event;
}

static void escalera_dispatch_next_event(Escalera* handle)
{
	escalera_event nextEvent;
	nextEvent = escalera_get_next_event(handle);
	escalera_dispatch_event(handle, &nextEvent);
}

static EscaleraEventID escalera_get_timed_event_name(Escalera* handle, sc_eventid evid)
{
	if(evid == &handle->timeEvents.escalera_main_region_VALIDATION_DOWN_STOP_tev0_raised) {
		return Escalera_escalera_main_region_VALIDATION_DOWN_STOP_time_event_0;
	}
	if(evid == &handle->timeEvents.escalera_main_region_VALIDATION_UP_STOP_tev0_raised) {
		return Escalera_escalera_main_region_VALIDATION_UP_STOP_time_event_0;
	}
	if(evid == &handle->timeEvents.escalera_main_region_VALIDATION_UP_START_tev0_raised) {
		return Escalera_escalera_main_region_VALIDATION_UP_START_time_event_0;
	}
	if(evid == &handle->timeEvents.escalera_main_region_VALIDATION_DOWN_START_tev0_raised) {
		return Escalera_escalera_main_region_VALIDATION_DOWN_START_time_event_0;
	}
	if(evid == &handle->timeEvents.escalera_main_region_VALIDATION_ADD_UP_1_tev0_raised) {
		return Escalera_escalera_main_region_VALIDATION_ADD_UP_1_time_event_0;
	}
	if(evid == &handle->timeEvents.escalera_main_region_VALIDATION_ADD_UP_2_tev0_raised) {
		return Escalera_escalera_main_region_VALIDATION_ADD_UP_2_time_event_0;
	}
	if(evid == &handle->timeEvents.escalera_main_region_VALIDATION_ADD_DOWN_1_tev0_raised) {
		return Escalera_escalera_main_region_VALIDATION_ADD_DOWN_1_time_event_0;
	}
	if(evid == &handle->timeEvents.escalera_main_region_VALIDATION_ADD_DOWN_2_tev0_raised) {
		return Escalera_escalera_main_region_VALIDATION_ADD_DOWN_2_time_event_0;
	}
	return Escalera_invalid_event;
}

