{
	/* Default exit sequence for state COMENZAR OFF */
	handle->stateConfVector[0] = Ej7_last_state;
}

/* Default exit sequence for state TERMINAR ON */
static void exseq_S_TERMINAR_ON(Ej7* handle)
{
	/* Default exit sequence for state TERMINAR ON */
	handle->stateConfVector[0] = Ej7_last_state;
}

/* Default exit sequence for state PUERTA ABIERTA ON  */
static void exseq_S_PUERTA_ABIERTA_ON_(Ej7* handle)
{
	/* Default exit sequence for state PUERTA ABIERTA ON  */
	handle->stateConfVector[0] = Ej7_last_state;
}

/* Default exit sequence for state PUERTA CERRADA ON */
static void exseq_S_PUERTA_CERRADA_ON(Ej7* handle)
{
	/* Default exit sequence for state PUERTA CERRADA ON */
	handle->stateConfVector[0] = Ej7_last_state;
}

/* Default exit sequence for region S */
static void exseq_S(Ej7* handle)
{
	/* Default exit sequence for region S */
	/* Handle exit of all possible states (of ej7.S) at position 0... */
	switch(handle->stateConfVector[ 0 ])
	{
		case Ej7_S_IDLE :
		{
			exseq_S_IDLE(handle);
			break;
		}
		case Ej7_S_MODO_OFF :
		{
			exseq_S_MODO_OFF(handle);
			break;
		}
		case Ej7_S_COMENZAR_ON :
		{
			exseq_S_COMENZAR_ON(handle);
			break;
		}
		case Ej7_S_TERMINAR_OFF :
		{
			exseq_S_TERMINAR_OFF(handle);
			break;
		}
		case Ej7_S_PUERTA_CERRADA_OFF :
		{
			exseq_S_PUERTA_CERRADA_OFF(handle);
			break;
		}
		case Ej7_S_PUERTA_ABIERTA_OFF :
		{
			exseq_S_PUERTA_ABIERTA_OFF(handle);
			break;
		}
		case Ej7_S_MODO_ON :
		{
			exseq_S_MODO_ON(handle);
			break;
		}
		case Ej7_S_COMENZAR_OFF :
		{
			exseq_S_COMENZAR_OFF(handle);
			break;
		}
		case Ej7_S_TERMINAR_ON :
		{
			exseq_S_TERMINAR_ON(handle);
			break;
		}
		case Ej7_S_PUERTA_ABIERTA_ON_ :
		{
			exseq_S_PUERTA_ABIERTA_ON_(handle);
			break;
		}
		case Ej7_S_PUERTA_CERRADA_ON :
		{
			exseq_S_PUERTA_CERRADA_ON(handle);
			break;
		}
		default: break;
	}
}

/* Default react sequence for initial entry  */
static void react_S__entry_Default(Ej7* handle)
{
	/* Default react sequence for initial entry  */
	enseq_S_IDLE_default(handle);
}


static sc_integer react(Ej7* handle, const sc_integer transitioned_before)
{
	/* State machine reactions. */
	SC_UNUSED(handle);
	return transitioned_before;
}

static sc_integer S_IDLE_react(Ej7* handle, const sc_integer transitioned_before)
{
	/* The reactions of state IDLE. */
 			sc_integer transitioned_after = transitioned_before;
	if ((transitioned_after) < (0))
	{ 
		if (handle->iface.evPuertaCerrada_raised == bool_true)
		{ 
			exseq_S_IDLE(handle);
			enseq_S_PUERTA_CERRADA_ON_default(handle);
			react(handle, 0);
			transitioned_after = 0;
		}  else
		{
			if (handle->iface.evPuertaAbierta_raised == bool_true)
			{ 
				exseq_S_IDLE(handle);
				enseq_S_PUERTA_ABIERTA_ON__default(handle);
				react(handle, 0);
				transitioned_after = 0;
			} 
		}
	} /* If no transition was taken then execute local reactions */
	if ((transitioned_after) == (transitioned_before))
	{ 
		transitioned_after = react(handle, transitioned_before);
	} return transitioned_after;
}

static sc_integer S_MODO_OFF_react(Ej7* handle, const sc_integer transitioned_before)
{
	/* The reactions of state MODO OFF. */
 			sc_integer transitioned_after = transitioned_before;
	if ((transitioned_after) < (0))
	{ 
		if (((handle->iface.evModoC_raised) == bool_true) && (((handle->internal.viModoCoccion) < (EJ7_EJ7IFACE_THRMODO)) == bool_true))
		{ 
			exseq_S_MODO_OFF(handle);
			ej7_opLED(handle, handle->internal.viModoCoccion, EJ7_EJ7IFACE_LED_OFF);
			handle->internal.viModoCoccion += 1;
			enseq_S_MODO_ON_default(handle);
			react(handle, 0);
			transitioned_after = 0;
		}  else
		{
			if (((handle->iface.evModoC_raised) == bool_true) && (((handle->internal.viModoCoccion) == (EJ7_EJ7IFACE_THRMODO)) == bool_true))
			{ 
				exseq_S_MODO_OFF(handle);
				ej7_opLED(handle, handle->internal.viModoCoccion, EJ7_EJ7IFACE_LED_OFF);
				handle->internal.viModoCoccion = EJ7_EJ7IFACE_MODODEFAULT;
				enseq_S_MODO_ON_default(handle);
				react(handle, 0);
				transitioned_after = 0;
			}  else
			{
				if (handle->iface.evComenzarTerminar_raised == bool_true)
				{ 
					exseq_S_MODO_OFF(handle);
					enseq_S_COMENZAR_ON_default(handle);
					react(handle, 0);
					transitioned_after = 0;
				}  else
				{
					if (handle->iface.evPuertaAbierta_raised == bool_true)
					{ 
						exseq_S_MODO_OFF(handle);
						enseq_S_PUERTA_ABIERTA_OFF_default(handle);
						react(handle, 0);
						transitioned_after = 0;
					} 
				}
			}
		}
	} /* If no transition was taken then execute local reactions */
	if ((transitioned_after) == (transitioned_before))
	{ 
		transitioned_after = react(handle, transitioned_before);
	} return transitioned_after;
}

