static void tp_eventqueue_init(tp_eventqueue * eq, tp_event *buffer, sc_integer capacity)
{
	eq->events = buffer;
	eq->capacity = capacity;
	eq->push_index = 0;
	eq->pop_index = 0;
	eq->size = 0;
}

static sc_integer tp_eventqueue_size(tp_eventqueue * eq)
{
	return eq->size;
}

static tp_event tp_eventqueue_pop(tp_eventqueue * eq)
{
	tp_event event;
	if(tp_eventqueue_size(eq) <= 0) {
		tp_event_init(&event, TP_invalid_event);
	}
	else {
		event = eq->events[eq->pop_index];
		
		if(eq->pop_index < eq->capacity - 1) {
			eq->pop_index++;
		} 
		else {
			eq->pop_index = 0;
		}
		eq->size--;
	}
	return event;
}
static sc_boolean tp_eventqueue_push(tp_eventqueue * eq, tp_event ev)
{
	if(tp_eventqueue_size(eq) == eq->capacity) {
		return bool_false;
	}
	else {
		eq->events[eq->push_index] = ev;
		
		if(eq->push_index < eq->capacity - 1) {
			eq->push_index++;
		}
		else {
			eq->push_index = 0;
		}
		eq->size++;
		
		return bool_true;
	}
}
static void tp_event_init(tp_event * ev, TPEventID name)
{
	ev->name = name;
	ev->has_value = bool_false;
}

static void tP_add_event_to_queue(tp_eventqueue * eq, TPEventID name)
{
	tp_event event;
	tp_event_init(&event, name);
	tp_eventqueue_push(eq, event);
}

static void tP_dispatch_event(TP* handle, const tp_event * event) {
	switch(event->name) {
		case TP_evTECXNoOprimido:
		{
			handle->iface.evTECXNoOprimido_raised = bool_true;
			break;
		}
		case TP_evTECXOprimido:
		{
			handle->iface.evTECXOprimido_raised = bool_true;
			handle->iface.evTECXOprimido_value = event->value.TP_evTECXOprimido_value;
			break;
		}
		case TP_internal_siTECXOK:
		{
			handle->internal.siTECXOK_raised = bool_true;
			break;
		}
		case TP_TP_TECX_DEBOUNCE_time_event_0:
		{
			handle->timeEvents.tP_TECX_DEBOUNCE_tev0_raised = bool_true;
			break;
		}
		case TP_TP_main_region_APAGADO_ITH_MENOR_time_event_0:
		{
			handle->timeEvents.tP_main_region_APAGADO_ITH_MENOR_tev0_raised = bool_true;
			break;
		}
		case TP_TP_main_region_ENCENDIDO_time_event_0:
		{
			handle->timeEvents.tP_main_region_ENCENDIDO_tev0_raised = bool_true;
			break;
		}
		default:
			break;
	}
}

static tp_event tP_get_next_event(TP* handle)
{
	tp_event next_event;
	tp_event_init(&next_event, TP_invalid_event);
	if(tp_eventqueue_size(&(handle->internal_event_queue)) > 0) {
		next_event = tp_eventqueue_pop(&(handle->internal_event_queue));
	}
	else if(tp_eventqueue_size(&(handle->in_event_queue)) > 0) {
		next_event = tp_eventqueue_pop(&(handle->in_event_queue));
	}
	return next_event;
}

static void tP_dispatch_next_event(TP* handle)
{
	tp_event nextEvent;
	nextEvent = tP_get_next_event(handle);
	tP_dispatch_event(handle, &nextEvent);
}

static TPEventID tP_get_timed_event_name(TP* handle, sc_eventid evid)
{
	if(evid == &handle->timeEvents.tP_TECX_DEBOUNCE_tev0_raised) {
		return TP_TP_TECX_DEBOUNCE_time_event_0;
	}
	if(evid == &handle->timeEvents.tP_main_region_APAGADO_ITH_MENOR_tev0_raised) {
		return TP_TP_main_region_APAGADO_ITH_MENOR_time_event_0;
	}
	if(evid == &handle->timeEvents.tP_main_region_ENCENDIDO_tev0_raised) {
		return TP_TP_main_region_ENCENDIDO_time_event_0;
	}
	return TP_invalid_event;
}


static void tp_event_value_init(tp_event * ev, TPEventID name, void * value)
{
	ev->name = name;
	ev->has_value = bool_true;
	
	switch(name)
	{
		case TP_evTECXOprimido:
			ev->value.TP_evTECXOprimido_value = *((sc_integer*)value);
			break;
		default:
			break;
	}
}

static void tP_add_value_event_to_queue(tp_eventqueue * eq, TPEventID name, void * value)
{
	tp_event event;
	tp_event_value_init(&event, name, value);
	tp_eventqueue_push(eq, event);
}
